import React from 'react';
import {
  BsBookmarkStar,
  BsBoxArrowRight,
  BsChevronRight,
  BsCoin,
  BsFolder2Open,
  BsPerson,
} from 'react-icons/bs';
import { useSelector } from 'react-redux';
import { useDispatch } from 'react-redux';
import { Link } from 'react-router-dom';
import { toast } from 'react-toastify';
import { logout } from '../../store/actions/userActions';

const profileLinks = [
  {
    name: 'Basic Info',
    link: '/profile/basic-info',
    icon: <BsPerson className="me-3 fs-4" />,
  },
  {
    name: 'Buy Credits',
    link: '/profile/buy-credits',
    icon: <BsCoin className="me-3 fs-4" />,
  },
  {
    name: 'Verify Badge',
    link: '/profile/verify-badge',
    icon: <BsBookmarkStar className="me-3 fs-4" />,
  },
  {
    name: 'Manage Ads',
    link: '/profile/manage-ads',
    icon: <BsFolder2Open className="me-3 fs-4" />,
  },
];

const ProfileOffcanvas = () => {
  const dispatch = useDispatch();
  const { user } = useSelector((state) => state.auth);
  return (
    <div
      className="offcanvas offcanvas-end"
      tabIndex="-1"
      data-bs-backdrop="true"
      id="account-sidebar"
      aria-labelledby="account-sidebar-label"
      style={{ zIndex: 1051 }}
    >
      <div className="offcanvas-header">
        {/* <!-- user logged in --> */}
        {user ? (
          <div className="container-fluid d-flex gap-3 align-items-center">
            <img
              src="data:image/jpeg;base64,"
              alt=""
              className="rounded-circle border border-5"
              height="60px"
              width="60px"
            />
            <div>
              <h5>{user.name}</h5>
              <Link to="/profile">Manage account</Link>
            </div>
          </div>
        ) : (
          <div className="d-grid col-10 flex-fill">
            <Link
              to="/login"
              type="button"
              className="btn btn-outline-primary shadow-none"
            >
              Log In
            </Link>
          </div>
        )}

        <button
          type="button"
          className="btn-close ms-3 me-2 shadow-none"
          data-bs-dismiss="offcanvas"
          aria-label="Close"
        ></button>
      </div>
      <hr className="m-0" />
      <div className="h-100 overflow-auto mt-3">
        <ul className="list-group">
          {profileLinks.map((link) => (
            <li
              key={link.name}
              className={`list-group-item list-group-item-action border-0 ${
                !user ? 'd-none' : ''
              }`}
            >
              <Link
                to={link.link}
                className="nav-link text-secondary d-flex justify-content-between align-items-center"
              >
                <span>
                  {link.icon}
                  {link.name}
                </span>
                <span>
                  <BsChevronRight />
                </span>
              </Link>
            </li>
          ))}
        </ul>
      </div>
      {user && (
        <div
          onClick={() => {
            dispatch(logout());
            toast('Logout Successfully', { type: 'success' });
          }}
          className="list-group-item list-group-item-action border-0"
        >
          <button className="btn btn-outline-danger my-3 py-2 w-100 d-flex justify-content-between align-items-center">
            <span>
              <BsBoxArrowRight className="me-2" />
              Logout
            </span>
            <span>
              <BsChevronRight />
            </span>
          </button>
        </div>
      )}
    </div>
  );
};

export default ProfileOffcanvas;
